package uz.gita.pokemoncompose.diimport dagger.Bindsimport dagger.Moduleimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport uz.gita.pokemoncompose.utils.navigation.AppNavigatorimport uz.gita.pokemoncompose.utils.navigation.AppNavigatorDispatcherimport uz.gita.pokemoncompose.utils.navigation.AppNavigatorHandlerimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)interface NavigationModule {    @[Binds Singleton]    fun bindsAppNav(impl: AppNavigatorDispatcher): AppNavigator    @[Binds Singleton]    fun bindsAppNavHandler(impl: AppNavigatorDispatcher): AppNavigatorHandler}