package uz.gita.pokemoncompose.diimport android.content.Contextimport com.chuckerteam.chucker.api.ChuckerInterceptorimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.android.qualifiers.ApplicationContextimport dagger.hilt.components.SingletonComponentimport okhttp3.OkHttpClientimport retrofit2.Retrofitimport retrofit2.converter.gson.GsonConverterFactoryimport uz.gita.pokemoncompose.data.source.remote.api.MainApiimport uz.gita.pokemoncompose.utils.Constants.BASE_URLimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)class NetworkModule {    @[Provides Singleton]    fun providesClient(@ApplicationContext context: Context): OkHttpClient =        OkHttpClient.Builder().addInterceptor(ChuckerInterceptor.Builder(context).build()).build()    @[Provides Singleton]    fun providesRetrofit(client: OkHttpClient): Retrofit =        Retrofit.Builder()            .baseUrl(BASE_URL)            .addConverterFactory(GsonConverterFactory.create())            .client(client)            .build()    @[Provides Singleton]    fun providesApi(retrofit: Retrofit): MainApi = retrofit.create(MainApi::class.java)}