package uz.gita.pokemoncompose.domain.usecase.implimport kotlinx.coroutines.flow.Flowimport uz.gita.pokemoncompose.domain.modules.MainModelimport uz.gita.pokemoncompose.domain.repository.MainRepositoryimport uz.gita.pokemoncompose.domain.usecase.MainUseCaseimport javax.inject.Injectclass MainUseCaseImpl @Inject constructor(    private val repository: MainRepository): MainUseCase {    override fun getCategories(): Flow<Result<List<String>>> = repository.getCategories()    override fun getCategoryData(category: String): Flow<Result<List<MainModel>>> = repository.getCategoryData(category)}