package uz.gita.pokemoncompose.presentation.screens.detailimport android.annotation.SuppressLintimport androidx.compose.foundation.layout.Boximport androidx.compose.foundation.layout.fillMaxSizeimport androidx.compose.foundation.lazy.LazyColumnimport androidx.compose.foundation.lazy.itemsimport androidx.compose.material3.CircularProgressIndicatorimport androidx.compose.material3.Textimport androidx.compose.runtime.Composableimport androidx.compose.runtime.collectAsStateimport androidx.compose.ui.Alignmentimport androidx.compose.ui.Modifierimport androidx.compose.ui.text.TextStyleimport androidx.compose.ui.tooling.preview.Previewimport androidx.compose.ui.unit.spimport cafe.adriel.voyager.androidx.AndroidScreenimport cafe.adriel.voyager.hilt.getViewModelimport uz.gita.pokemoncompose.presentation.components.Itemimport uz.gita.pokemoncompose.ui.theme.PokemonComposeThemeclass DetailScreen(private val category: String) : AndroidScreen() {    private var isCheck = true    @Composable    override fun Content() {        val viewModel: DetailContract.ViewModel = getViewModel<DetailViewModel>()        if (isCheck) {            viewModel::onEventDispatcher.invoke(DetailContract.Intent.SetCategory(category))            viewModel::onEventDispatcher.invoke(DetailContract.Intent.GetAll)            isCheck = false        }        MainContent(viewModel.uiState.collectAsState().value)    }}@Composableprivate fun MainContent(    uiState: DetailContract.UIState = DetailContract.UIState(),) {    Box(modifier = Modifier.fillMaxSize()) {        if (uiState.isProgress) {            CircularProgressIndicator(                modifier = Modifier                    .align(Alignment.Center)            )        }        if (uiState.isShow) {            Text(                modifier = Modifier                    .align(Alignment.Center),                text = "Empty",                fontSize = 20.sp            )        }        LazyColumn {            items(uiState.data) {                Item(it.name)            }        }    }}@SuppressLint("UnrememberedMutableState")@Composable@Preview(showBackground = true)private fun MainContentPreview() {    PokemonComposeTheme {        MainContent()    }}