package uz.gita.pokemoncompose.presentation.screens.mainimport uz.gita.pokemoncompose.presentation.screens.detail.DetailScreenimport uz.gita.pokemoncompose.utils.navigation.AppNavigatorimport javax.inject.Injectinterface MainDirection {    suspend fun moveToDetail(category: String)}class MainDirectionImpl @Inject constructor(    private val appNavigator: AppNavigator,) : MainDirection {    override suspend fun moveToDetail(category: String) {        appNavigator.openWithSave(DetailScreen(category))    }}